var host = flow.get('$parent.host');
var port = flow.get('$parent.port');

var master_info = flow.get('$parent.Master_Info');
//var master_info = msg.master_info;
var num_brick = master_info.number_bricks;

var slave_info = flow.get('$parent.Slave_Info');
//var slave_info = msg.slave_info;
var offset_mosi = slave_info.offset_mosi;
var offset_miso = slave_info.offset_miso;


var upd0 = (num_brick+6)%256;
var upd1 = parseInt((num_brick+6)/256)
var num = parseInt(num_brick) - 1;
const upd3 = new Array(2);
//upd3.writeInt16BE(offset_mosi[num], 0);
var off_miso = offset_miso[num];
if(off_miso < 3){
    off_miso = 3;
}
//var update = Buffer.from([upd0, upd1, 16, 0, 0, 0, 0,3]);
var update = Buffer.from([upd0, upd1, 16, 0, 0, 0, offset_mosi[num], off_miso]);
//var upd = Buffer.concat([update, upd3]);

var output = flow.get("$parent.Output");
var output_eth = Buffer.concat([update, output]);
msg.payload = output_eth;
msg.host = host;
msg.port = port;
return msg;

var master_info =flow.get('$parent.Master_Info');
var num_brick = master_info.number_bricks;

var slave_info = flow.get('$parent.Slave_Info');
var offset_miso = slave_info.offset_miso;
var data_miso = slave_info.data_miso;
var data_mosi = slave_info.data_mosi;
var length = 0;

var input = Object.values(msg.payload);
var brick_input = new Array(num_brick);
for (var i = 0; i < num_brick; i++)
{
    length = length + data_miso[i];
    brick_input[i] = [];
    //slave_output.payload.bricks[i] = [];
    for(var j = (8+offset_miso[i]); j < (8+length);  j++)
    {
        brick_input[i].push(input[j]);
        //slave_output.payload.bricks[i].push(msg.payload.buffer[j]);
    }
}

flow.set("$parent.Brick_Input", brick_input);
flow.set("$parent.sys_approved",1);
//msg.payload = brick_input;

return msg;

var id = (msg.payload[8] <<8) + msg.payload[9];
var id_1 = parseInt(((msg.payload[8] <<8) + msg.payload[9])/1000);
var id_2 = id % 1000;

var master_input;
master_input = {"number_bricks": msg.payload[6],
"master_status": msg.payload[7],
"master_id": id_1 + "-" + id_2,
"prot_ver": msg.payload[10],
"soft_ver": msg.payload[11],
"man_id": msg.payload[12],
"res1": msg.payload[13],
"res2": msg.payload[14]};

flow.set('$parent.Master_Info', master_input);

var slave = {status:[], 
data_mosi:[], 
data_miso:[], 
prot_ver: [], 
hard_rev: [],
dev_id: [],
man_id: [],
offset_mosi: [],
offset_miso: []};

for (var i = 0; i < msg.payload[6]; i++)
{
    slave.status[i] = msg.payload[15+(i*11)+0];
    slave.data_mosi[i] = msg.payload[15+(i*11)+1];
    slave.data_miso [i] = msg.payload[15+(i*11)+2];
    slave.prot_ver[i] = msg.payload[15+(i*11)+3];
    slave.hard_rev[i] = msg.payload[15+(i*11)+4];
    slave.dev_id[i] = parseInt(((msg.payload[15+(i*11)+5] <<8) + msg.payload[15+(i*11)+6])/1000) + "-" + (((msg.payload[15+(i*11)+5] <<8) + msg.payload[15+(i*11)+6]) % 1000); 
   // slave.dev_id[i] = (msg.payload[15+(i*11)+5] << 8) + msg.payload[15+(i*11)+6];
    slave.man_id[i] = (msg.payload[15+(i*11)+7] << 8) + msg.payload[15+(i*11)+8];
    slave.offset_mosi[i] = msg.payload[15+(i*11)+9]; 
    slave.offset_miso[i] = msg.payload[15+(i*11)+10];
}

flow.set('$parent.Slave_Info', slave);

var num_brick = msg.payload[6];
var sum = 0
for (i=0; i<num_brick; i++)
{
    sum += slave.data_mosi[i];
}
var output = Buffer.allocUnsafe(sum);
output.fill(0);

flow.set("$parent.Output", output);

msg.master_info = master_input;
msg.slave_info = slave;

return msg;

var host = flow.get('$parent.host');
var port = flow.get('$parent.port');
msg.host = host;
msg.port = port;
return msg;
